# 传输信息
def sendWechat(AgentId, Secret, CompanyId, Touser='@all', Title='标题', Desc='内容', Url='URL', Bthtxt='查看'):
    # 获取 ACCESS_TOKEN
    Res = requests.post(
        f'https://qyapi.weixin.qq.com/cgi-bin/gettoken?corpid={CompanyId}&corpsecret={Secret}').json()
    ACCESS_TOKEN = Res["access_token"]

    # 要发送的信息格式
    sendData = {
        "touser": Touser,
        "msgtype": "textcard",
        "agentid": f"{AgentId}",
        "textcard": {
            "title": Title,
            "description": Desc,
            "url": Url,
            "btntxt": Bthtxt
        }
    }

    # 发送消息
    sendUrl = f'https://qyapi.weixin.qq.com/cgi-bin/message/send?access_token={ACCESS_TOKEN}'
    sendData = json.dumps(sendData)
    sendRes = requests.post(sendUrl, data=sendData)
    print(sendRes)


